// UsersList.js

import { useEffect, useState } from "react";
import { useAuthContext } from '../hooks/useAuthContext';

// components
import UserDetails from '../components/UserDetails';

const UsersList = () => {
    const [users, setUsers] = useState(null);
    const { user } = useAuthContext();
    const currentUser = user._id

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                // Replace 'currentUserId' with the actual user's ID
                //const currentUserId = '12345'; // Replace with your logic to get the user's ID
                //?currentUserId=${currentUserId}
                const response = await fetch(`/api/user/`);
                const json = await response.json();

                if (response.ok) {
                    setUsers(json.users);
                }
            } catch (error) {
                console.error("Error fetching users:", error);
            }
        }

        // if (user) {
        fetchUsers();
        // }
    }, []);

    // // Filter users based on whether the current user is following them
    // const usersNotFollowing = users ? users.filter((user) => {
    //     return !user.following.includes(user._id); // Assuming you have a followers array in each user object
    // }) : [];

    // const usersFollowing = users ? users.filter((user) => {
    //     return user.following.includes(user._id); // Assuming you have a followers array in each user object
    // }) : [];

    // Filter users based on whether the current user is following them
const usersNotFollowing = users ? users.filter((user) => {
    return !user.following.includes(currentUser._id.toString()); // Convert currentUser._id to a string
}) : [];

const usersFollowing = users ? users.filter((user) => {
    return user.following.includes(currentUser._id.toString()); // Convert currentUser._id to a string
}) : [];


    return (
        <div className="split-users-list">
            <div className="users-list">
                <h3>Users</h3>
                {/* {users && users.map((user) => (
                    <UserDetails key={user._id} user={user} />
                ))} */}
                {usersNotFollowing.map((user) => (
                    <UserDetails key={user._id} user={user} />
                ))}
            </div>
            <div className="users-list">
                <h3>Following</h3>
                {/* {users && users.map((user) => (
                    <UserDetails key={user._id} user={user} />
                ))} */}
                {usersFollowing.map((user) => (
                    <UserDetails key={user._id} user={user} />
                ))}
            </div>
        </div>
    );
};

export default UsersList;
